import React, { useState, useRef, useEffect } from "react";
import Arrow from "../../Assets/arrow.png";

function Collapse(props) {
  const [toggle, setToggle] = useState(false);
  const [heightEl, setHeightEl] = useState("0px");
  const contentRef = useRef(null);

  useEffect(() => {
    if (toggle) {
      setHeightEl(`${contentRef.current.scrollHeight}px`);
    } else {
      setHeightEl("0px");
    }
  }, [toggle, props.aproposText]);

  useEffect(() => {
    // Attendez que le contenu soit correctement rendu avant de mesurer sa hauteur
    if (contentRef.current) {
      setHeightEl(`${contentRef.current.scrollHeight}px`);
    }
  }, [props.aproposText]);

  const toggleState = () => {
    setToggle(!toggle);
  };

  return (
    <div className={`collapse ${props.aproposStyle}`}>
      <div onClick={toggleState} className="collapse__visible">
        <h2>{props.aproposTitle}</h2>
        <img
          className={toggle ? "arrow rotated" : "arrow"}
          src={Arrow}
          alt="arrow"
        />
      </div>
      <div
        ref={contentRef}
        className={`collapse__toggle ${toggle ? "animated" : ""}`}
        style={{ height: heightEl }}
      >
        <p aria-hidden={toggle ? "true" : "false"}>{props.aproposText}</p>
      </div>
    </div>
  );
}

export default Collapse;
