import React, { useState } from 'react';
import Arrow from "../../Assets/arrow.png";

function Collapse({title, content}) {
  const [selectedItem, setSelectedItem] = useState(null);

  const menuItems = [
    { id: 1, text: "Texte pour l'élément 1" },
    { id: 2, text: "Texte pour l'élément 2" },
    { id: 3, text: "Texte pour l'élément 3" }
  ];

  const handleClick = (item) => {
    setSelectedItem(item.text);
  };

  return (
    <div>
      <div className="menu">
        {menuItems.map(item => (
          <button key={item.id} onClick={() => handleClick(item)}>{item.text}</button>
        ))}
      </div>
      <div className="content">
        {selectedItem && (
          <p>{selectedItem}</p>
        )}
      </div>
    </div>
  );
}

export default Collapse;
