import { useState, useRef } from 'react';
import Arrow from "../../Assets/arrow.png";

function Collapse({title, content}) {
  const [collapseState, setCollapseState] = useState({
    active: false,
    maxHeight: '0px'
  });

  const contentCollapse = useRef(null);

  const toggleCollapse = () => {
    const newActiveState = !collapseState.active;
    const newMaxHeight = newActiveState ? `${contentCollapse.current.scrollHeight}px` : '0px';
    setCollapseState({ active: newActiveState, maxHeight: newMaxHeight });
  };

  return (
    <div className="collapse-section">
      <button className={`collapse ${collapseState.active ? 'active' : ''}`} onClick={toggleCollapse}>
        <span className="collapse-title">{title}</span>
        <img src={Arrow} className={`collapse-icon ${collapseState.active ? 'rotate' : ''}`} alt="" />
      </button>
      <div ref={contentCollapse} style={{ maxHeight: collapseState.maxHeight }} className="collapse-content">
        <div className="collapse-text">
          {content.map((item, index) => (
            <div key={index}>{item}</div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default Collapse;
